package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//for -> to perform the same task repeatedly
		
		for (int i = 10; i >= 1 ; i--) { //i++ -> increment operator
										// i = i+1
										// i = 0+1 = 1 
						//0 < 5 -> true
			
			System.out.println(i);
			
			/*
			 * 
			 */
			
			/* 1st iteration ->  i= 0, 0<5 -> true
			 * 
			 * for{
			 * print the number 10			Console: 10
			 * }
			 * i++ -> i= i+1 -> 0+1 =1
			 * 2nd iteration
			 * i=1, 1 < 5-> true
			 * for{
			 * print 10						Console: 10
			 * }
			 * i++ -> 1+1 = 2
			 * 3rd iteration
			 * i = 3
			 * 
			 * 
			 * 
			 * 
			 * i=4 
			 * i++ 4+1 = 5
			 * Next iteration
			 * 
			 *i = 5, 5<5 -> false
			 *loop is terminated
			 *
			 *
			 *i = 10, 10 < 10
			 *
			 * 
			 */
			
		}

	}

}
